        return new Source("{% extends 'base.html.twig' %}

{% block title %}Season index{% endblock %}

{% block stylesheet %} {{ parent() }} {% endblock %}

{% block body %}
    <h1>Season index</h1>

    <table class=\"table\">
        <thead>
            <tr>
                <th>Id</th>
                <th>Number</th>
                <th>Year</th>
                <th>Description</th>
                <th class=\"action\">actions</th>
            </tr>
        </thead>
        <tbody>
        {% for season in seasons %}
            <tr>
                <th colspan=\"5\" class=\"title\">
                    {{ season.program.title }}
                </th>
            </tr>
            <tr>
                <td>{{ season.id }}</td>
                <td>{{ season.number }}</td>
                <td>{{ season.year }}</td>
                <td>{{ season.description }}</td>
                <td>
                    <a href=\"{{ path('season_show', {'id': season.id}) }}\">show</a>
                    <a href=\"{{ path('season_edit', {'id': season.id}) }}\">edit</a>
                </td>
            </tr>
        {% else %}
            <tr>
                <td colspan=\"5\">no records found</td>
            </tr>
        {% endfor %}
        </tbody>
    </table>

    <a href=\"{{ path('season_new') }}\">Create new</a>
{% endblock %}
